import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
const Home = () => {

    const [getuserdata,setUserdata] =useState([]);
    
    const getdata =async(e)=>{
      const res =await fetch("/getdata",{
        method:"GET",
        headers:{
          "Content-Type":"application/json"
        }
      });
        const data =await res.json();
        if(res.status ===422 || !data){
          console.log("error");
        }
        else{
          setUserdata(data)
          console.log("get data");
        }

    }

    useEffect(()=>{
      getdata();
    },[])




  return (
    <div>
      <div className="mt-5">
        <div className="container">
          <div className="add_btn mt-2">
            <Link to='/register'>
            <button className="btn btn-primary mb-3">Add data</button>
            </Link>
            
          </div>
          <table class="table">
            <thead>
              <tr className="table-dark">
                <th scope="col">Id</th>
                <th scope="col">Username</th>
                <th scope="col">Email</th>
                <th scope="col">job</th>
                <th scope="col">Number</th>
                <th scope="col"></th>
              </tr>
            </thead>
            <tbody>
              {
                getuserdata.map((data,id)=>{
                  return(
                    <>
                    <tr>
                <th scope="row">{id+1}</th>
                <td>{data.name}</td>
                <td>{data.email}</td>
                <td>{data.work}</td>
                <td>{data.mobile}</td>
                <td className="d-flex justify-content-between">
                  <NavLink to={`view/${data._id}`}>    <button className="btn btn-success"><i className="fa-solid fa-eye"></i></button> </NavLink>
                    <button className="btn btn-primary"><i className="fa-solid fa-pen-to-square"></i></button>
                    <button className="btn btn-danger"><i className="fa-solid fa-trash"></i></button>
                </td>
              </tr>
                    </>
                  )
                })
              }
              
             
             
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Home;
