import React from 'react'
import CreateIcon from '@mui/icons-material/Create';
import DeleteIcon from '@mui/icons-material/Delete';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import PhoneAndroidIcon from '@mui/icons-material/PhoneAndroid';
import WorkIcon from '@mui/icons-material/Work';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
const Details = () => {
  const [getuserdata,setUserdata] =useState([]);
    
  const {id} =useParams("");

    const getdata =async()=>{
      const res =await fetch(`/getuser`,{
        method:"GET",
        headers:{
          "Content-Type":"application/json"
        }
      });
        const data =await res.json();
        if(res.status ===422 || !data){
          console.log("error");
        }
        else{
          setUserdata(data)
          console.log("get data");
        }

    }

    useEffect(()=>{
      getdata();
    },[])


  return (
    <div>
      <div className="container mt-5">
        <h1 style={{ fontweight:400 }}>Welcome MernStack Developer</h1>
        <Card sx={{maxWidth:600}}>
        
            <CardContent>
           
                <div className="row">
                <div className='add_btn mr-3'>
                    <button className='btn btn-primary mx-2'><CreateIcon/></button>
                    <button className='btn btn-danger mx-2 '><DeleteIcon/></button>
                </div>
                <div className="left_view col-lg-6 col-md-6 col-12 ">
                <img src="https://img.freepik.com/premium-vector/man-avatar-profile-picture-vector-illustration_268834-538.jpg"  style={{width:70 }}/>
                <h3 className='mt-3'>Name: <span> Developer </span></h3>
                <h3 className='mt-3'>Age: <span> 21 </span></h3>
                <p className='mt-3'><MailOutlineIcon/> Email: <span>Lucky@gamil.com</span></p>
                <p className='mt-3'><WorkIcon/> Occupation: <span>Mern Stack developer</span></p>

                </div>
                <div className="right_view col-lg-6 col-md-6 col-12 mt-5">
                    <p className='mt-5'><LocationOnIcon/> location: <span>Ghaziabad</span></p>
                    <p className='mt-3'><PhoneAndroidIcon/> mobile: <span>9398075922</span></p>
                    <p className='mt-3'>Description:<span>Lorem ipsum, dolor sit amet consectetur adipisicing elit. aut omnis nt?</span> </p>

                </div>
                </div>
            </CardContent>
        </Card>
      </div>
    </div>
  )
}

export default Details
