import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from "react-router-dom";
import RemoveRedEyeIcon from '@mui/icons-material/RemoveRedEye';
import CreateIcon from '@mui/icons-material/Create';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import { adddata, deldata } from './context/ContextProvider';
import { updatedata } from './context/ContextProvider';

const Home = () => {
    const [getuserdata, setUserdata] = useState([]);
    const { udata, setUdata } = useContext(adddata);
    const { updata, setUPdata } = useContext(updatedata);
    const { dltdata, setDLTdata } = useContext(deldata);

    const getdata = async (e) => {
        const res = await fetch("/getdata", {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            }
        });
        const data = await res.json();
        if (res.status === 422 || !data) {
            console.log("error");
        } else {
            setUserdata(data);
            console.log("get data");
        }
    }

    useEffect(() => {
        getdata();
    }, []);

    const deleteuser = async (id) => {
        const res2 = await fetch(`/deleteuser/${id}`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json"
            }
        });

        const deletedata = await res2.json();
        console.log(deletedata);

        if (res2.status === 422 || !deletedata) {
            console.log("error");
        } else {
            console.log("user deleted");
            setDLTdata(deletedata);
            getdata();
        }
    }

    useEffect(() => {
        if (udata) {
            const timer = setTimeout(() => {
                setUdata(null); // Clear the alert after 1 second
            }, 1000);

            return () => clearTimeout(timer); // Clear the timer on component unmount
        }
    }, [udata, setUdata]);

    useEffect(() => {
        if (updata) {
            const timer = setTimeout(() => {
                setUPdata(null); // Clear the alert after 1 second
            }, 1000);

            return () => clearTimeout(timer); // Clear the timer on component unmount
        }
    }, [updata, setUPdata]);

    useEffect(() => {
        if (dltdata) {
            const timer = setTimeout(() => {
                setDLTdata(null); // Clear the alert after 1 second
            }, 1000);

            return () => clearTimeout(timer); // Clear the timer on component unmount
        }
    }, [dltdata, setDLTdata]);

    return (
        <>
            {udata && (
                <div className="alert alert-success alert-dismissible fade show" role="alert">
                    <strong>{udata.name}</strong> added successfully!
                    <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
                </div>
            )}

            {updata && (
                <div className="alert alert-success alert-dismissible fade show" role="alert">
                    <strong>{updata.name}</strong> updated successfully!
                    <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
                </div>
            )}

            {dltdata && (
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                    <strong>{dltdata.name}</strong> deleted successfully!
                    <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
                </div>
            )}

            <div className="mt-5">
                <div className="container">
                    <div className="add_btn mt-2 mb-2">
                        <NavLink to="/register" className="btn btn-primary">Add data</NavLink>
                    </div>

                    <table className="table">
                        <thead>
                            <tr className="table-dark">
                                <th scope="col">id</th>
                                <th scope="col">Username</th>
                                <th scope="col">email</th>
                                <th scope="col">Job</th>
                                <th scope="col">Number</th>
                                <th scope="col"></th>
                            </tr>
                        </thead>
                        <tbody>
                            {getuserdata.map((element, id) => (
                                <tr key={id}>
                                    <th scope="row">{id + 1}</th>
                                    <td>{element.name}</td>
                                    <td>{element.email}</td>
                                    <td>{element.work}</td>
                                    <td>{element.mobile}</td>
                                    <td className="d-flex justify-content-between">
                                        <NavLink to={`view/${element._id}`}>
                                            <button className="btn btn-success"><RemoveRedEyeIcon /></button>
                                        </NavLink>
                                        <NavLink to={`edit/${element._id}`}>
                                            <button className="btn btn-primary"><CreateIcon /></button>
                                        </NavLink>
                                        <button className="btn btn-danger" onClick={() => deleteuser(element._id)}>
                                            <DeleteOutlineIcon />
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default Home;
